'        const tasks         = document.getElementById(target).querySelectorAll(''.TASK'');',
'        //',
'        tasks.forEach(function(task) {',
'            sorted.push(task.getAttribute(''id'').replace(''TASK_'', ''''));',
'        });',
'        sorted = sorted.join('':'');',
'        //',
